package variable;

public class Datatype {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		   
	        int intVar = 3076;
	        float floatVar = 20f;
	        byte byteVar = 5;
	        short shortVar = 240;
	        long longVar = 60;
	        double doubleVar = 20.123;
	        boolean booleanVar = true;
	        char charVar ='a';
	        String strvar="sadar";
	    
	        System.out.println("int      "+intVar);
	        System.out.println( "flt     "+floatVar );
	        System.out.println( "byt     "+byteVar);
	        System.out.println("shrvar   "+shortVar);
	        System.out.println("long     "+longVar);
	        System.out.println("doub     "+doubleVar);
	        System.out.println( "bol     "+booleanVar);
	        System.out.println("har       "+charVar);
	        System.out.println("str       "+strvar);
	}

}
